operations.body:
mutation createPart($workId: String!) {
  createPart(workId: $workId) {
    id
    authorID
    workID
    name
    createdAt
    __typename
  }
}

mutation createWork {
  createWork {
    id
    name
    createdAt
    updatedAt
    authorID
    __typename
  }
}

query getBlocks($partId: ID!) {
  getPartById(partId: $partId) {
    id
    blocks {
      id
      partID
      speed
      speaker
      texts
      duration
      pitch
      volume
      authorID
      __typename
    }
    __typename
  }
}

query getParts($workId: ID!) {
  getWorkById(workId: $workId) {
    id
    parts {
      id
      authorID
      workID
      name
      createdAt
      __typename
    }
    __typename
  }
}

query getWorkName($workId: ID!) {
  getWorkById(workId: $workId) {
    id
    name
    __typename
  }
}

query getWorks {
  getUserFromUserId {
    id
    works {
      id
      name
      createdAt
      updatedAt
      authorID
      __typename
    }
    __typename
  }
}

mutation updatePart($name: String!, $partId: ID!) {
  updatePart(name: $name, partId: $partId) {
    id
    authorID
    workID
    name
    createdAt
    __typename
  }
}

mutation updateWork($name: String!, $workId: ID!) {
  updateWork(name: $name, workId: $workId) {
    name
    id
    authorID
    createdAt
    updatedAt
    __typename
  }
}
